    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        String resposta = JOptionPane.showInputDialog(null, "Digite o código do animal", "Editar animal", JOptionPane.QUESTION_MESSAGE);

        int id = 0;

        if (resposta != null) {

            try {
                id = Integer.parseInt(resposta);
            } catch (Exception e) {
                System.err.println("O ID " + resposta + " é inválido");
            }
            

            openAnimal(id);

        }
    }//GEN-LAST:event_jButton1ActionPerformed

