    private void openAnimal(final int id) {
        // new AnimalEditarFrame(id);

        // EVENT DISPATCH THREAD

        new SwingWorker() {

            @Override
            protected Object doInBackground() throws Exception {
                new AnimalEditarFrame(id);
                return Void.TYPE;
            }

        }.execute();
        

    }
